import React, { Fragment } from "react";
import 'C:\Users\Ryzen\Desktop\reacted\my-app\public\css\product.scss'

function Product(){
    return (
        <Fragment>
            <div className="navbar">
            <div className="container">
                <a className="logo" href="index.html"><span>RMH</span>Shop</a>
                
                <img id="mobile-cta" className="mobile-menu" src="./images/menu.svg" alt="Open navigation"/>

                <nav>
                    <img id="mobile-exit" className="mobile-menu-exit" src="./images/exit.svg" alt="Close navigation"/>
                    <ul className="primary-nav">
                        <li className="current"><a href="index.html">Home</a></li>
                        <li><a href="products.html">Products</a></li>
                    </ul>

                    <ul className="secondary-nav">
                        <li><a href="sign-in.html">Sign-in</a></li>
                        <li><a href="userpage.html" id="userid">User not logged in</a></li>
                    </ul>

                </nav>
            </div>
        </div>


        <div className="item-container">
            <div className="item-image">
              <img src="images/Ronchik.jpg" alt="Item image"/>
            </div>
            <div className="item-info">
              <h1 className="item-name">Крутий Пес</h1>
              <p className="item-description">
                Найліпший пес якого я коли небудь бачив,
                такого ні у кого немає, а у кого є,
                той бреше. Це звичайний лорем іпсум, а не опис
                плейсхолдер так би мовити, база, ґрунт,
                опора, фундамент, моноліт, наріжний камінь.
              </p>
              <p className="item-price">$9.99</p>
              <button className="item-add-to-cart">Купити</button>
            </div>
          </div>

          {/*
          <script>
            if (localStorage.username != null) {
            document.getElementById("userid").innerHTML = localStorage.username;
            }
            </script>

        <script>
            const mobileBtn = document.getElementById('mobile-cta')
                nav = document.querySelector('nav');
                mobileBtnExit = document.getElementById('mobile-exit');

            mobileBtn.addEventListener('click', ()=> {
                nav.classList.add('menu-btn');
            })

            mobileBtnExit.addEventListener('click', ()=> {
                nav.classList.remove('menu-btn');
            })

        </script>
        */}
        </Fragment>
    )
}

export default Product;
