import React, { Fragment, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./css/userpage.css";

function Userpage() {
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    if (localStorage.username != null) {
      document.getElementById("userid").innerHTML = localStorage.username;
    }
  }, []);

  const handleMobileBtnClick = () => {
    document.querySelector("nav").classList.add("menu-btn");
  };

  const handleMobileBtnExitClick = () => {
    document.querySelector("nav").classList.remove("menu-btn");
  };

  const handleLogout = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch("http://127.0.0.1:5000/user/logout", {
        method: "POST",
      });

      if (response.ok) {
        localStorage.clear();
        window.location.reload(true);
        window.location.replace("/");
        window.location.href = "/";
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleCreditCardSubmit = (event) => {
    event.preventDefault();

    // Perform credit card submission logic here

    // Show the message after successful submission
    setShowMessage(true);

    // Hide the message after a certain time
    setTimeout(() => {
      setShowMessage(false);
    }, 5000);
  };

  return (
    <Fragment>
      <div className="body_userpage">
        <div className="navbar">
          <div className="container">
            <Link className="logo" to="/">
              <span>RMH</span>Shop
            </Link>

            <img
              id="mobile-cta"
              className="mobile-menu"
              src="./images/menu.svg"
              alt="Open navigation"
              onClick={handleMobileBtnClick}
            />

            <nav>
              <img
                id="mobile-exit"
                className="mobile-menu-exit"
                src="./images/exit.svg"
                alt="Close navigation"
                onClick={handleMobileBtnExitClick}
              />
              <ul className="primary-nav">
                <li>
                  <Link to="/">Home</Link>
                </li>
                <li>
                  <Link to="/Products">Products</Link>
                </li>
              </ul>

              <ul className="secondary-nav">
                <li>
                  <Link to="/SignIn">Signin</Link>
                </li>
                <li>
                  <Link to="/Userpage" id="userid">
                    User not logged in
                  </Link>
                </li>
              </ul>
            </nav>
          </div>
        </div>
        <div className="buttoncont">
          <button className="logoutbtn" id="logout1" onClick={handleLogout}>
            Log out
          </button>
        </div>
      </div>

      {showMessage && (
        <div className="message fade-out">
          <p>Card info added</p>
        </div>
      )}
    </Fragment>
  );
}

export default Userpage;
